import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../actions';

class Products extends Component {
    async componentDidMount() {
        this.props.getproduct()
    }
    render() {
        return(
            <div>
                Product!!!!!!!!!!!!!!!!!!!!
                <br/>
                <div class="list-group">
                    <a href="/edit" class="list-group-item list-group-item-action flex-column align-items-start active">
                        <div class="d-flex w-100 justify-content-between">
                        <h5 class="mb-1">Title: {this.props.title}</h5>
                        </div>
                        <p class="mb-1">Description: {this.props.description}</p>
                        <small>Price: {this.props.price}</small> <small>Company: {this.props.company}</small>
                    </a>         
                </div>
                <nav>
                <ul class="nav nav-pills pull-right">
                    <li role="presentation" id="addHyperLink"><a href="/addPost">Add</a></li>
                </ul>
                </nav>
            </div>
            
            
        );
    }
}

class ShowPost extends React.Component {
    constructor(props) {
      super(props);
      this.updatePost = this.updatePost.bind(this);
      this.deletePost = this.deletePost.bind(this);
      this.getPost = this.getPost.bind(this);
      this.state = {
        posts:[]
      };
      
    }

    updatePost(id){
      hashHistory.push('/addPost/' + id);
    }

    deletePost(id){
      if(confirm('Are you sure ?')){
        var self = this;
        axios.post('/deletePost', {
          id: id
        })
        .then(function (response) {
          self.getPost();
        })
        .catch(function (error) {
          console.log('Error is ',error);
        });
      }
    }

    getPost(){
      var self = this;
      axios.post('/getPost', {
      })
      .then(function (response) {
        console.log('res is ',response);
        self.setState({posts:response.data})
      })
      .catch(function (error) {
        console.log('error is ',error);
      });
    }
    

    componentDidMount(){
      this.getPost();

      document.getElementById('homeHyperlink').className = "active";
      document.getElementById('addHyperLink').className = "";
    }
    
    render() {

      return(
          <table className="table table-striped">
            <thead>
              <tr>
                <th>#</th>
                <th>Title</th>
                <th>Subject</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {
                this.state.posts.map(function(post,index) {
                   return <tr key={index} >
                            <td>{index+1}</td>
                            <td>{post.title}</td>
                            <td>{post.subject}</td>
                            <td>
                              <span onClick={this.updatePost.bind(this,post._id)} className="glyphicon glyphicon-pencil"></span>
                            </td>
                            <td>
                              <span onClick={this.deletePost.bind(this,post._id)} className="glyphicon glyphicon-remove"></span>
                            </td>
                          </tr>
                }.bind(this))
              }
            </tbody>
          </table>
        )
    }
}

function mapStateToProps(state) {
    return {
        title: state.item.products.title,
        description : state.item.products.description,
        price: state.item.products.price,
        company: state.item.products.company
    }
}



export default connect(mapStateToProps, actions)(Products);
