import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../actions';

class Products extends Component {
    async componentDidMount() {
        this.props.getproduct()
    }
    render() {
        return(
            <div>
                Product!!!!!!!!!!!!!!!!!!!!
                <br/>

                title:<h3>{this.props.title}</h3>
                description:<h3>{this.props.description}</h3>
                price:<h3>{this.props.price}</h3>
                company:<h3>{this.props.company}</h3>
                company:<h3>{this.props.company}</h3>
                <div id="btnContainer">
                <button class="btn" onclick="listView()"><i class="fa fa-bars"></i> List</button> 
                <button class="btn active" onclick="gridView()"><i class="fa fa-th-large"></i> Grid</button>
                </div>
                <div class="row">
  <div class="column" style="background-color:#aaa;">
    <h2>Column 1</h2>
    <p>Some text..</p>
  </div>
  <div class="column" style="background-color:#bbb;">
    <h2>Column 2</h2>
    <p>Some text..</p>
  </div>
</div>
            </div>
            
            
        );
    }
}

function mapStateToProps(state) {
    return {
        title: state.item.products.title,
        description : state.item.products.description,
        price: state.item.products.price,
        company: state.item.products.company
    }
}

export default connect(mapStateToProps, actions)(Products);
