import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../actions';

class Products extends Component {
    async componentDidMount() {
        this.props.getproduct()
    }
    render() {
        return(
            <div>
                Product!!!!!!!!!!!!!!!!!!!!
                <br/>

                title:<h3>{this.props.title}</h3>
                description:<h3>{this.props.description}</h3>
                price:<h3>{this.props.price}</h3>
                company:<h3>{this.props.company}</h3>
                company:<h3>{this.props.company}</h3>
                <div>
                <li>
                    <div class="wrapimg">
                    <div class="addPro">
                    </div>
                    </div>
                    <div class="titlePro"> <a href="javascript:;">Samsung EC ST500</a>
                    <span>$ 145.00</span>
                    <p class="descTitle">Detailed item information</p>
                    <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.</p>
                    </div>
                    </li>
                </div>
            </div>
            
        );
    }
}

function mapStateToProps(state) {
    return {
        title: state.item.products.title,
        description : state.item.products.description,
        price: state.item.products.price,
        company: state.item.products.company
    }
}

export default connect(mapStateToProps, actions)(Products);
