import { AUTH_SIGN_UP } from '../actions/types'

const DEFAULT_STATE = {
    isAuthenticated: false,
    token: '',
    errorMessage: ''
}

export default (state = DEFAULT_STATE, action) => {
    switch(action.type){
        case AUTH_SIGN_UP:
            return { ...state, token: action.payload }
        default:
        return state
    }
    return state;
}
