import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../actions';

class Products extends Component {
    async componentDidMount() {
        this.props.getproduct()
    }
    render() {
        return(
            <div>
                Product!!!!!!!!!!!!!!!!!!!!
                <br/>
                title:<h3>{this.props.title}</h3>
                description:<h3>{this.props.description}</h3>
                price:<h3>{this.props.price}</h3>
                company:<h3>{this.props.company}</h3>
                company:<h3>{this.props.company}</h3>
                <div id="btnContainer">
                <button id="btn" onclick="listView()"><i class="fa fa-bars"></i> List</button> 
                <button id="btn active" onclick="gridView()"><i class="fa fa-th-large"></i> Grid</button>
                </div>
                <br></br>
                <div class="row">
                <div class="column">
                    <h2>Column 1</h2>
                    <p>Some text..</p>
                </div>
                <div class="column">
                    <h2>Column 1</h2>
                    <p>Some text..</p>
                </div>
                <div class="column">
                    <h2>Column 1</h2>
                    <p>Some text..</p>
                </div>
                <div class="column">
                    <h2>Column 1</h2>
                    <p>Some text..</p>
                </div>
                <div class="column">
                    <h2>Column 1</h2>
                    <p>Some text..</p>
                </div>
                </div>
            </div>
            
            
        );
    }
}

function mapStateToProps(state) {
    return {
        title: state.item.products.title,
        description : state.item.products.description,
        price: state.item.products.price,
        company: state.item.products.company
    }
}

// Get the elements with class="column"
var elements = document.getElementsByClassName("column");

// Declare a loop variable
var i;

// List View
function listView() {
  for (i = 0; i < elements.length; i++) {
    elements[i].style.width = "100%";
  }
}

// Grid View
function gridView() {
  for (i = 0; i < elements.length; i++) {
    elements[i].style.width = "50%";
  }
}

/* Optional: Add active class to the current button (highlight it) */
var container = document.getElementById("btnContainer");
var btns = container.getElementsByClassName("btn");
for (var i = 0; i < btns.length; i++) {
  btns[i].addEventListener("click", function() {
    var current = document.getElementsByClassName("active");
    current[0].className = current[0].className.replace(" active", "");
    this.className += " active";
  });
}

export default connect(mapStateToProps, actions)(Products);
